function createfigure(x, y_lost, y_dominate)
%CREATEFIGURE(x,y_lost,y_dominate)
%  x:  bar xvector
%  y_lost:  bar yvector
%  y_dominate:  bar yvector

%  Auto-generated by MATLAB on 20-Mar-2012 17:31:48

% Create figure
figure1 = figure;

% Create axes
axes1 = axes('Parent',figure1,'FontSize',14);
% Uncomment the following line to preserve the X-limits of the axes
xlim(axes1,[-8.17385057471264 160]);
% Uncomment the following line to preserve the Y-limits of the axes
ylim(axes1,[-4.1 4.1]);

box(axes1,'on');
hold(axes1,'all');

% Create bar
bar(x,y_lost,'FaceColor',[0 0 0],'BarWidth',0.7,'Parent',axes1,...
    'DisplayName','Clone lost');

% Create bar
bar(x,y_dominate,'FaceColor',[0.7 0.7 0.7],'BarWidth',0.7,...
    'Parent',axes1,...
    'DisplayName','Clone dominates');

% Create ylabel
ylabel('Number of simulations','FontSize',16);

% Create xlabel
xlabel('Simulation duration (days)','FontSize',16);

% Create legend
legend(axes1,'show');

set(gca,'YTickLabel',{'10000','1000','100','10','1','10','100','1000','10000'})